import dotenv from "dotenv";
dotenv.config();

import { default as axios } from "axios";

export const moviesApis = axios.create({
    baseURL: "https://api.themoviedb.org/3/",
    headers: {
        Authorization: `Bearer ${process.env.TMDB_API_TOKEN}`
    }

})
